// Import the necessary modules
import React from 'react';
import { View, Text, StyleSheet } from 'react-native';

import ListCategoryGrouped from './uicomponents/ListCategoryGrouped';
const Detail = ({ route }) => {
    // Use the useTheme hook to get the theme object
    const { theme } = useTheme();
    // Use the route.params property to get the item that was passed from the previous screen
    const { item } = route.params;
    // Use the custom hook to fetch the data from the API based on the item.title
    const { data, error, isLoading, isError } = useFetchDataByTitle(item.title);
    // Render the UI of the detail screen
    return (
      <View style={styles.container}>
        {isLoading && (
          <Text style={[styles.loading, { color: theme.colors.primary }]}>
            Loading...
          </Text>
        )}
        {isError && (
          <Text style={[styles.error, { color: theme.colors.error }]}>
            Error: {error.message}
          </Text>
        )}
        {data && (
          <View style={styles.dataContainer}>
            <Text style={[styles.dataTitle, { color: theme.colors.text }]}>
             ali
            </Text>
            {/* ... render the rest of the data as you wish */}
          </View>
        )}
      </View>
    );
  };
  
  // Define the styles for the component using StyleSheet
  const styles = StyleSheet.create({
    container: {
      flex: 1,
      alignItems: "center",
      justifyContent: "center",
    },
    loading: {
      fontSize: 20,
      fontWeight: "bold",
    },
    error: {
      fontSize: 20,
      fontWeight: "bold",
    },
    dataContainer: {
      margin: 10,
    },
    dataTitle: {
      fontSize: 18,
      fontWeight: "bold",
      marginVertical: 5,
    },
    dataSubtitle: {
      fontSize: 16,
      marginVertical: 5,
    },
  });
  
  // Export the component
  export default Detail;
