// Import the necessary modules
import React, { useEffect, useLayoutEffect } from 'react';
import { View, Text, StyleSheet } from 'react-native';

import { Button, Card, ListItem, useTheme } from '@rneui/themed';
import PriceDifferShow from '../uicomponents/PriceDiffer';


interface ProductPriceInfoProps {
    productPriceInfo: any;
    increidiblePrice: number
}
const ProductPriceInfo: React.FC<ProductPriceInfoProps> = ({ productPriceInfo, increidiblePrice }) => {
    const { theme } = useTheme();
    return (
        <>
            <Text style={[styles.dataTitle, { color: theme.colors.text }]}>{productPriceInfo.price}</Text> |
            <Text style={[styles.dataTitle, { color: theme.colors.text }]}>{productPriceInfo.rrpPrice}</Text> |
            <Text style={[styles.dataTitle, { color: theme.colors.text }]}>({productPriceInfo.discountPercent}%)</Text>
            <PriceDifferShow priceA={increidiblePrice} priceB={productPriceInfo.p_price} />
        </>
    );
};
const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: "center",
        justifyContent: "center",
    },
    loading: {
        fontSize: 20,
        fontWeight: "bold",
    },
    error: {
        fontSize: 20,
        fontWeight: "bold",
    },
    dataContainer: {
        margin: 10,
    },
    dataTitle: {
        fontSize: 18,
        fontWeight: "bold",
        marginVertical: 5,
    },
    dataSubtitle: {
        fontSize: 16,
        marginVertical: 5,
    },
});

// Export the component
export default ProductPriceInfo;
