import React from 'react';

const CategoryTitles = ({ items }) => {
  return (
    <div>
      <h2>Items</h2>
      <ul>
        {items.map((item, index) => (
          <li key={index}>{item.name}</li>
        ))}
      </ul>
    </div>
  );
};

export default CategoryTitles;
