import React from 'react';
interface ListItemComponentProps {
  item: category_grouped;
  index: number;
  // Replace with the type of your theme object
}
import { Card, ListItem, Divider, useTheme } from '@rneui/themed';
import { category_grouped } from '../entity/OrdersProductsCategoriesInfo ';
import { useNavigation } from "@react-navigation/native";


const ListCategoryGrouped: React.FC<ListItemComponentProps> = ({ item, index }) => {
  // Use the useNavigation hook to get the navigation object
  const navigation = useNavigation();
  // Define the detail component
  const detail = ({ route }) => {
    // Use the route.params property to get the item that was passed from the previous screen
    const { item } = route.params;
    // ... use the item to render the UI of the detail screen
  };

  return (
    <ListItem key={index} bottomDivider>
      <ListItem.Content>
        <ListItem.Title onPress={() => {
          navigation.navigate("detail", { item });
        }}>
          {item.title}
        </ListItem.Title>
        <ListItem.Subtitle >
          Count: {item.count} | Max Price: {item.maxPrice} | Avg Price: {item.avgPrice} | Total Price: {item.totalPrice} | Avg Discount: {item.avgDiscount}
        </ListItem.Subtitle>
      </ListItem.Content>
    </ListItem>
  );
};

export default ListCategoryGrouped;
