export const convertDateToShamsi = (gregorianDate) => {
    const [year, month, day] = gregorianDate.split('-');
    const date = new Date(year, month - 1, day);
    const options = {
      year: 'numeric',
      month: 'numeric',
      day: 'numeric',
      calendar: 'persian',
      numberingSystem: 'latn'
    };
    return new Intl.DateTimeFormat('fa-IR', options).format(date);
  }
